def rewrite PassiveVoice(sentence):
    # Split the sentence into words
    words = sentence.split()

    # Identify the passive voice form
    if 'was' in words or 'were' in words:
        verb = None
        for word in words:
            if word.endswith('ed'):
                verb = word[:-2]

        active_part = ''
        for i, (word, x) in enumerate(words):
            if x == 'play':
                active_part += f'{verb} the song. '
            else:
                break

        passive_part = f'The radio played {active_part.strip()}.'

    return ' '.join(words + [passive_part])

